import React, { Component } from "react";
import axios from "axios";

export class MainHeader extends Component {
  constructor(props) {
    super(props);
    this.state = {
      id: "",
      username: "",
      password: "",
      newsletter: false,
      isLoggedIn: false,
    };
    this.handleSingIn = this.handleSingIn.bind(this);
    this.handleSingOut = this.handleSingOut.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    this.setState({
      [event.target.name]: event.target.value,
    });
  }
  /*
  childFunction = (e) => {
    e.preventDefault();
    this.setState({ isLoggedIn: true });
    this.props.callFromParent(this.state.isLoggedIn);
    //console.log(this.state.isLoggedIn);
  };
  */
  handleSingOut(event) {
    event.preventDefault();
    localStorage.clear();
    window.location.href = "/";
    this.props.callFromParent(false);
  }
  handleSingIn(event) {
    const { username, password } = this.state;

    axios
      .post("http://localhost:9000/users", {
        user: {
          username: username,
          password: password,
        },
      })
      .then((response) => {
        console.log(response);

        if (response.data === "invalid") {
          console.log("Error!!");
        } else {
          console.log(response.data.newsletter);
          var loggedInUser = {
            id: response.data.id,
            username: response.data.username,
            newsletter: response.data.newsletter,
          };
          this.setState({
            isLoggedIn: true,
            newsletter: response.data.newsletter,
          });
          this.props.callFromParent(
            true,
            this.state.username,
            this.state.newsletter,
            this.state.id
          );
          //this.props.username === this.username;
          //console.log(this.state.isLoggedIn);
          localStorage.setItem(
            "currentLoggedInUser",
            JSON.stringify(loggedInUser)
          );
        }
      });
    event.preventDefault();
  }

  render() {
    const textStyle = {
      color: "#fff",
      textAlign: "center",
    };
    const headerStyle = {
      background: "#5e438d",
      height: "130px",
      padding: "10px",
    };
    const formStyle = {
      color: "#fff",
      textAlign: "left",
    };
    const buttonStyle = {
      width: "100px",
    };
    const registerButtonStyle = {
      width: "100px",
      float: "right",
    };

    if (this.state.isLoggedIn === false) {
      return (
        <div>
          <header style={headerStyle}>
            <h1 style={textStyle}>Newsletter</h1>
            <form onSubmit={this.handleSingIn} style={formStyle}>
              Username{" "}
              <input
                type="text"
                name="username"
                value={this.state.username}
                onChange={this.handleChange}
              ></input>{" "}
              Password{" "}
              <input
                type="password"
                name="password"
                value={this.state.password}
                onChange={this.handleChange}
              ></input>{" "}
              <button
                //onClick={this.childFunction.bind(this)}
                style={buttonStyle}
                type="submit"
              >
                Login
              </button>
            </form>
            <button style={registerButtonStyle}>Register</button>
          </header>
        </div>
      );
    } else if (this.state.isLoggedIn === true) {
      return (
        <div>
          <header style={headerStyle}>
            <h1 style={textStyle}>Newsletter</h1>
            <form onSubmit={this.handleSingOut} style={formStyle}>
              <button style={buttonStyle} type="submit">
                Sign Out
              </button>
            </form>
            <button style={registerButtonStyle}>Register</button>
          </header>
        </div>
      );
    }
  }
}

export default MainHeader;
